import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import Avatar from "@mui/material/Avatar";
import { red } from "@mui/material/colors";
import { Stack } from "@mui/material";

export default function FollowingProfile({ name, bio }) {
  return (
    <Card sx={{ width: "100%" }}>
      <CardHeader
        avatar={
          <Avatar sx={{ bgcolor: red[500] }} aria-label="following-name">
            R
          </Avatar>
        }
        title={name}
        subheader={bio}
      />
    </Card>
  );
}

export const FollowingList = () => {
  const peopleList = [
    { title: "Bruce Banner", subheader: "Smash" },
    { title: "Thor", subheader: "Worthy" },
    { title: "Bruce Banner", subheader: "Smash" },
    { title: "Thor", subheader: "Worthy" },
    { title: "Bruce Banner", subheader: "Smash" },
    { title: "Thor", subheader: "Worthy" },
  ];

  return (
    <>
      <Stack
        width={"100vw"}
        justifyContent={"center"}
        alignItems={"center"}
        spacing={2}
        // height={"100vh"}
        minHeight={"100vh"}
        overflow={"auto"}
        mt={2}
        sx={{ marginLeft: { xs: "0px", sm: "75px", md: "200px", lg: "200px" } }}
      >
        {peopleList.map((d) => {
          return <FollowingProfile name={d.title} bio={d.subheader} />;
        })}
      </Stack>
    </>
  );
};
