import {
  useTheme,
  useMediaQuery,
  Grid,
  Button,
  Stack,
  Typography,
  IconButton,
} from "@mui/material";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import EditIcon from "@mui/icons-material/Edit";


function SimpleBottomNavigation() {
  const [value, setValue] = React.useState(0);

  return (
    <Box>
      <BottomNavigation
        sx={{ justifyContent: "space-around" }}
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        <BottomNavigationAction
          to={"/profile/posts"}
          component={Link}
          value={"/posts"}
          label="Posts"
          icon={<DynamicFeedIcon />}
        />
        <BottomNavigationAction
          to={"/profile/interactions"}
          component={Link}
          value={"/interactions"}
          label="Interactions"
          icon={<FavoriteIcon />}
        />
        <BottomNavigationAction
          to={"/profile/media"}
          component={Link}
          value={"/media"}
          label="Media"
          icon={<ImageIcon />}
        />
      </BottomNavigation>
    </Box>
  );
}

export const ProfileDetails = () => {
  const theme = useTheme();
  const matches = useMediaQuery(theme.breakpoints.up("md"));
  const widthCalc = `calc(100vw - ${matches ? "200" : "75"}px)`;
  return (
    <Grid container>
      <Grid
        width={widthCalc}
        justifyContent={"center"}
        alignItems={"center"}
        item
        container
      >
        <Grid
          sm={8}
          item
          container
          justifyContent={"space-around"}
          direction={"row"}
          spacing={1}
        >
          <Grid sm={2} item container justifyContent={"center"}>
            <IconButton color={"primary"}>
              <AccountCircleIcon sx={{ fontSize: "100px" }} />
            </IconButton>
          </Grid>
          <Grid sm={8} item>
            <Stack direction="column">
              <Typography color={"#fff"} variant={"h4"}>
                Dr. Stephen Strange
              </Typography>
              <Typography color={"#ddd"} variant={"subtitle1"}>
                @drstrange
              </Typography>
            </Stack>
          </Grid>
          <Grid sm={2} item>
            <IconButton color={"primary"} variant={"outlined"}>
              <EditIcon />
            </IconButton>
          </Grid>
        </Grid>
      </Grid>
      <Grid item ml={2.5}>
        <Typography variant="body1" color="#fff">
          MD. Sorcerer Supreme. Avenger.
        </Typography>
      </Grid>
      <Grid item ml={2.5} sm={12}>
        <Stack direction={"row"} spacing={3}>
          <Stack direction="row" spacing={0.5}>
            <Typography variant="subtitle2" color={"#fff"}>
              10M
            </Typography>
            <Typography variant="subtitle2" color={"#ddd"}>
              Followers
            </Typography>
          </Stack>
          <Stack direction="row" spacing={0.5}>
            <Typography variant="subtitle2" color={"#fff"}>
              23
            </Typography>
            <Typography variant="subtitle2" color={"#ddd"}>
              Following
            </Typography>
          </Stack>
        </Stack>
      </Grid>
      <Grid item sm={12}>
        <SimpleBottomNavigation />
      </Grid>
    </Grid>
  );
};
