import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import Avatar from "@mui/material/Avatar";
import { red } from "@mui/material/colors";
import { Stack, Typography, useTheme } from "@mui/material";

export default function FollowingProfile({ title, subheader }) {
  return (
    <Card>
      <CardHeader
        avatar={
          <Avatar sx={{ bgcolor: red[500] }} aria-label="following-name">
            {title[0]}
          </Avatar>
        }
        title={title}
        subheader={subheader}
      />
    </Card>
  );
}

export const FollowingList = () => {
  const theme = useTheme();

  const peopleList = [
    { title: "Bruce Banner", subheader: "Smash" },
    { title: "Thor", subheader: "Worthy" },
    { title: "Bruce Banner", subheader: "Smash" },
    { title: "Thor", subheader: "Worthy" },
    { title: "Bruce Banner", subheader: "Smash" },
    { title: "Thor", subheader: "Worthy" },
  ];

  return (
    <>
      <Stack
        width={"100vw"}
        justifyContent={"center"}
        alignItems={"center"}
        spacing={2}
        // height={"100vh"}
        minHeight={"100vh"}
        overflow={"auto"}
        mt={2}
        sx={{ marginLeft: { xs: "0px", sm: "75px", md: "200px", lg: "200px" } }}
      >
        {peopleList.map((d) => {
            <FollowingList title={d.title} subheader={d.subheader} />
        })}
      </Stack>
    </>
  );
};
